var fs = require('fs');

var write = function(outputFilename, dataObject) {
    fs.writeFile(outputFilename, JSON.stringify(dataObject, null, 4), function (err) {
        if (err) {
            console.log(err);
        } else {
            console.log("JSON saved to " + outputFilename);
        }
    });
}//write

//---------- exports ---------------

exports.write = write;