var fs = require('fs');

var write = function(outputFilename, dataObject) {
    fs.writeFile(outputFilename, JSON.stringify(dataObject, null, 4), function (err) {
        if (err) {
            console.log(err);
        } else {
            console.log("JSON saved to " + outputFilename);
        }
    });
}//write

var read = function(outputFilename, callback){ 
	fs.readFile(outputFilename, 'utf8', callback);//read
}

function stampa(err, data) {
	if (err) throw err;	
	console.log(data);
}

//---------- exports ---------------

exports.write = write;
exports.read = read;